package com.example.xiangnanzhang.mymedalsdemo;import android.util.Log;public class PalLog {	public static final String TAG = "AAdemo";	public static final boolean DEBUG = true;	public static void printD(String message) {		printD(TAG, message);	}	public static void printE(String message) {		printE(TAG, message);	}	public static void printI(String message) {		printI(TAG, message);	}	public static void printW(String message) {		printW(TAG, message);	}	public static void printV(String message) {		printV(TAG, message);	}	public static void printD(String tag, String message) {		if (DEBUG) Log.d(tag, message);	}	public static void printE(String tag, String message) {		if (DEBUG) Log.e(tag, message);	}	public static void printI(String tag, String message) {		if (DEBUG) Log.i(tag, message);	}	public static void printW(String tag, String message) {		if (DEBUG) Log.w(tag, message);	}	public static void printV(String tag, String message) {		if (DEBUG) Log.v(tag, message);	}}